(35, 'woocommerce-WCSubscriptions-June-2023-updated-needed', 'marketing', 'en_US', 'Action required: Security update of WooCommerce Subscriptions', '<strong>Your store requires a security update for the WooCommerce Subscriptions plugin</strong>. Please update the WooCommerce Subscriptions plugin immediately to address a potential vulnerability.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(36, 'woocommerce-WCReturnsWarranty-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce Returns and Warranty Requests extension', '<strong>Your store requires a security update for the Returns and Warranty Requests extension</strong>.  Please update to the latest version of the WooCommerce Returns and Warranty Requests extension immediately to address a potential vulnerability discovered on May 31.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(37, 'woocommerce-WCOPC-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce One Page Checkout', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce One Page Checkout extension</strong>. The fix for this vulnerability was released for this extension on June 13th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(38, 'woocommerce-WCGC-July-2023-update-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce GoCardless Extension', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce GoCardless extension</strong>. The fix for this vulnerability was released on July 4th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(39, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(40, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(41, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(42, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(43, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(44, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(45, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(46, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(47, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'sirt-woo-2024-11-xss-admin', 'update', 'en_US', 'An important update is required for WooCommerce', 'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'paypal-braintree-sdk-depreciation-2025-02-20', 'update', 'en_US', 'Update required for your PayPal Braintree extension', 'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'uk-vat-notice-2025-04-30', 'warning', 'en_US', 'UK VAT numbers are no longer supported', 'Due to recent HM Revenue &amp; Customs (HMRC) VAT API changes, your EU VAT Number for WooCommerce extension can no longer validate UK VAT numbers. HMRC now requires merchants to register an API key, which this extension does not currently support. We recommend EU/UK VAT Validation Manager for WooCommerce as a replacement', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'payson-retirement-2025', 'update', 'en_US', 'Payson Form Retirement', 'Svea Bank is discontinuing the Payson service; the last date for transactions is August 31, 2025. As a result, the Payson Form extension has been removed from WooCommerce.com. Documentation remains available.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'paypal-braintree-sdk-depreciation-2025-05-30', 'update', 'en_US', 'Action required: Update the PayPal Braintree extension', 'Your PayPal Braintree extension is using an outdated SDK and will stop processing payments after June 30, 2025, due to a root SSL certificate expiration. To avoid service disruption, update to version 3.2.4 or newer immediately.', '{}', 'pending', 'woocommerce.com', '2025-11-03 01:46:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'wc-admin-mobile-app', 'info', 'en_US', 'Cài đặt ứng dụng di động Woo', 'Cài đặt ứng dụng di động WooCommerce để quản lý đơn đặt hàng, nhận thông báo bán hàng và xem các số liệu chính - mọi lúc mọi nơi.', '{}', 'unactioned', 'woocommerce-admin', '2025-11-08 06:04:50', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Bắt đầu chấp nhận thanh toán trên cửa hàng của bạn!', 'Nhận thanh toán với nhà cung cấp phù hợp với bạn - chọn từ hơn 100 cổng thanh toán cho WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2025-11-08 06:04:50', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'paypaladvantage_q4_2025', 'marketing', 'en_US', 'PayPal Checkout offer: Get 40% back on fees for 3 months', 'Download and activate PayPal Payments in your WooCommerce admin, then complete your first transaction by 15 December 2025 to get 40% back on PayPal transaction fees — up to £2,000 for 3 months. PayPal Checkout works with your existing checkout. See terms: paypal.com/uk/campaign/paypalmerchantpromotion/terms.', '{}', 'pending', 'woocommerce.com', '2025-11-08 06:04:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Hỗ trợ WooCommerce cải thiện với tính năng theo dõi sử dụng', 'Việc thu thập dữ liệu sử dụng cho phép chúng tôi cải thiện WooCommerce. Cửa hàng của bạn sẽ được xem xét khi chúng tôi đánh giá các tính năng mới, đánh giá chất lượng của bản cập nhật hoặc xác định xem một cải tiến có hợp lý hay không. Bạn luôn có thể truy cập <a href=\"http://localhost:8282/23IM031_Oanh-23IM015_Ki%E1%BB%81u/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Cài đặt</a> và chọn ngừng chia sẻ dữ liệu. <a href=\"https://woocommerce.com/usage-tracking?utm_medium=product\" target=\"_blank\">Đọc thêm</a> về dữ liệu chúng tôi thu thập.', '{}', 'unactioned', 'woocommerce-admin', '2025-11-11 06:15:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'wc-admin-woocommerce-payments', 'marketing', 'en_US', 'Hãy thử cách thức mới để được thanh toán', 'Chấp nhận thẻ tín dụng và thẻ ghi nợ một cách an toàn trên trang web của bạn. Quản lý giao dịch mà không cần rời khỏi bảng điều khiển WordPress của bạn. Chỉ với <strong>WooPayments</strong>.<br><br>Bằng cách nhấp vào \"Bắt đầu\", bạn đồng ý với <a href=\"https://wordpress.com/tos/\" target=\"_blank\">Điều khoản dịch vụ</a> của chúng tôi', '{}', 'unactioned', 'woocommerce-admin', '2025-11-11 06:15:53', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'reddit_q4_2025', 'marketing', 'en_US', 'New: Reach high-intent shoppers on Reddit', 'Sync your catalog and pixel with Reddit for WooCommerce. Launch your ads faster on Reddit and connect with their 400M+ weekly active users and shoppers who turn to this platform for authentic conversations and recommendations.', '{}', 'pending', 'woocommerce.com', '2025-11-18 02:28:37', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'bfcm_general_sale_q4_2025', 'marketing', 'en_US', 'Save up to 40% on extensions and themes', 'For a limited time, take up to 40% off nearly everything in our Marketplace. It’s the perfect moment to give your store a holiday boost and level up for 2026. Sale ends December 2, 2025, at 14:00 UTC.', '{}', 'pending', 'woocommerce.com', '2025-11-21 07:27:07', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'wc-admin-launch-checklist', 'info', 'en_US', 'Bạn đã sẵn sàng để ra mắt cửa hàng của mình?', 'Để đảm bảo bạn không bao giờ có cảm giác chìm đắm \"mình đã quên điều gì\", chúng tôi đã tập hợp danh sách kiểm tra trước khi ra mắt cần thiết.', '{}', 'unactioned', 'woocommerce-admin', '2025-12-02 00:10:38', NULL, 0, 'plain', '', 0, 0, 'info');
